<?php class Router{ public static function dispatch(){ session_start(); $c=$_GET['c']??'home'; $a=$_GET['a']??'index';
  $class=ucfirst($c).'Controller'; $file=__DIR__.'/../controllers/'.$class.'.php'; if(!file_exists($file)){ http_response_code(404); echo 'Controller not found'; exit; }
  require_once $file; $x=new $class(); if(!method_exists($x,$a)){ http_response_code(404); echo 'Action not found'; exit; } $x->$a(); }}